mapper.select:
/** nims-mysql-mapper|selectBsshInfos-마약류취급자정보 조회|julim */ SELECT bssh_cd, /* 마약류취급자식별번호 */ bssh_nm, /* 마약류취급자명 */ induty_nm, /* 업종명 */ hdnt_cd, /* 의료업자구분 */ hdnt_nm, /* 의료업자구분명 */ bizrno, /* 사업자등록번호 */ rprsntv_nm, /* 대표자명 */ chrg_nm, /* 담당자명 */ hptl_no, /* 요양기관번호 */ join_yn, /* 회원가입여부 */ bssh_stts_nm, /* 마약류취급자상태명 */ prmisn_no /* 허가번호 */ FROM tb_bssh_info
AND bssh_cd = #{bc}
AND INSTR(bssh_nm, #{bn}) > 0
AND bizrno = #{bi}
AND hptl_no = #{hp}

/** nims-mysql-mapper|selectProductInfo-상품 정보 조회|julim */ SELECT prduct_cd, /* 제품코드 */ prdlst_mst_cd, /* 제품대표코드 */ prduct_nm, /* 제품명 */ nrcd_se_nm, /* 마약항정구분 */ prtm_se_nm, /* 중점일반구분 */ prd_min_distb_qy, /* 최소유통단위수량 - 제품규격정보(고정값=1) */ std_packng_stle_nm, /* 제품최소유통단위 */ prd_tot_pce_qy, /* 제품총낱개단위수량 */ pce_co_unit_nm, /* 제품낱개단위 */ bssh_cd, /* 마약류취급자식별번호 */ rgs_dt, /* 등록일 */ upd_dt /* 변경일 */ FROM tb_prduct_info
AND prduct_cd = #{p}
AND INSTR(prduct_nm, #{pn}) > 0
AND prtm_se_nm = IF(#{fg2} = '1', '중점관리대상', '일반관리대상')

mapper.insert:
/** nims-mysql-mapper|insertDsuseMgt-폐기관리 생성|julim */
SELECT NVL(MAX(dscdmng_id), CONCAT(DATE_FORMAT(now(), '%Y%m'),'0000')) + 1 FROM tb_dsuse_mgt WHERE dscdmng_id LIKE CONCAT(DATE_FORMAT(now(), '%Y%m'), '%');
INSERT INTO tb_dsuse_mgt ( dscdmng_id, /* 폐기관리ID */ user_id, /* 사용자ID */ prgrs_stts_cd, /* 폐기관리진행상태코드 */ bssh_cd, /* 마약류취급자식별ID */ rnd_dtl_rpt_cnt, /* 수불상세보고수 */ hdr_de, /* 취급일자 */ rpt_de, /* 보고일자 */ dsuse_se_cd, /* 폐기구분코드 */ dsuse_prv_cd, /* 폐기사유코드 */ dsuse_mth_cd, /* 폐기방법코드 */ dsuse_loc, /* 폐기장소 */ dsuse_de, /* 폐기일자 */ use_yn, reg_dt, rgtr ) VALUES ( #{dscdmngId}, #{userId}, #{prgrsSttsCd}, #{bsshCd}, #{rndDtlRptCnt}, #{hdrDe}, #{rptDe}, #{dsuseSeCd}, #{dsusePrvCd}, #{dsuseMthCd}, #{dsuseLoc}, #{dsuseDe}, 'Y', DATE_FORMAT(now(), '%Y%m%d%H%i%s'), #{rgtr} )

mapper.select:
/** nims-mysql-mapper|selectDsuseMgts-폐기관리 목록 조회|julim  */

 WHERE tdm.dscdmng_id IN

    #{item}

/** nims-mysql-mapper|selectDsuseMgtDtls-폐기관리 상세 목록 조회|julim */ SELECT tdmd.dscdmng_id, /* 폐기관리ID */ tdmd.dscdmng_sn, /* 폐기관리일련번호 */ tdmd.prduct_cd, /* 제품코드 */ tdmd.prduct_nm, /* 제품명 */ tdmd.min_distb_qy, /* 최소유통단위수량 */ tdmd.pce_qy, /* 낱개단위수량 */ tdmd.mnf_no, /* 제조번호 */ tdmd.prd_valid_de, /* 제품유효기한일자 */ tdmd.mnf_seq, /* 제품일련번호 */ tdmd.dsuse_qy, /* 폐기수량 */ tdmd.usr_rpt_id_no, /* 사용자보고식별번호 */ tdmd.usr_rpt_ln_id_no, /* 사용자보고라인식별번호 */ tdmd.use_yn, tdmd.reg_dt, tdmd.rgtr, tpi.nrcd_se_nm, /* 마약항정구분 */ tpi.prtm_se_nm, /* 중점일반구분 */ tpi.std_packng_stle_nm, /* 제품최소유통단위 */ tpi.pce_co_unit_nm, /* 제품낱개단위명 */ tpi.bssh_cd, /* 제조수입자 */ tbi.bssh_nm /* 제조수입자명 */ FROM tb_dsuse_mgt_dtl tdmd LEFT JOIN tb_prduct_info tpi ON (tdmd.prduct_cd = tpi.prduct_cd) -- AND tdrid.use_yn = 'Y') LEFT JOIN tb_bssh_info tbi ON (tpi.bssh_cd = tbi.bssh_cd) WHERE tdmd.dscdmng_id = #{dscdmngId}
AND tdmd.dscdmng_sn = #{dscdmngSn}
AND tdmd.use_yn = #{useYn}
AND tdmd.userId = #{userId}
AND tdmd.usr_rpt_id_no = #{usrRptIdNo}

/** nims-mysql-mapper|selectDsuseRptInfoByUsrRptIdNo-폐기보고정보 데이타 조회|julim */ SELECT usr_rpt_id_no, /* 사용자보고식별번호 */ ref_usr_rpt_id_no, /* 참조사용자식별번호 */ bssh_cd, /* 마약류취급자식별번호 */ bssh_nm, /* 마약류취급자명 */ induty_nm, /* 업종명 */ rpt_ty_cd, /* 보고유형코드 */ rnd_dtl_rpt_cnt, /* 수불상세보고수 */ hdr_de, /* 취급일자 */ rpt_de, /* 보고일자 */ dsuse_se_cd, /* 폐기구분코드 */ dsuse_prv_cd, /* 폐기사유코드 */ dsuse_mth_cd, /* 폐기방법코드 */ dsuse_loc, /* 폐기장소 */ dsuse_de, /* 폐기일자 */ status, /* 상태 */ org_usr_rpt_id_no, /* 원본사용자보고식별번호 */ use_yn, reg_dt, rgtr FROM tb_dsuse_rpt_info WHERE usr_rpt_id_no = #{usrRptIdNo}
AND use_yn = #{useYn}
AND userId = #{userId}

/** nims-mysql-mapper|selectDsuseRptInfoDtls-폐기보고정보 상세 조회|julim */ SELECT tdrid.usr_rpt_id_no, /* 사용자보고식별번호 */ tdrid.usr_rpt_ln_id_no, /* 사용자보고라인식별번호 */ tdrid.prduct_cd, /* 제품코드 */ tdrid.prduct_nm, /* 제품명 */ tdrid.min_distb_qy, /* 최소유통단위수량 */ tdrid.pce_qy, /* 낱개단위수량 */ tdrid.mnf_no, /* 제조번호 */ tdrid.prd_valid_de, /* 제품유효기한일자 */ tdrid.mnf_seq, /* 제품일련번호 */ tdrid.dsuse_qy, /* 폐기수량 */ tdrid.use_yn, tdrid.reg_dt, tdrid.rgtr, tpi.nrcd_se_nm, /* 마약항정구분 */ tpi.prtm_se_nm, /* 중점일반구분 */ tpi.bssh_cd, /* 제조수입자 */ tbi.bssh_nm /* 제조수입자명 */ FROM tb_dsuse_rpt_info_dtl tdrid LEFT JOIN tb_prduct_info tpi ON (tdrid.prduct_cd = tpi.prduct_cd) -- AND tdrid.use_yn = 'Y') LEFT JOIN tb_bssh_info tbi ON (tpi.bssh_cd = tbi.bssh_cd) WHERE tdrid.usr_rpt_id_no = #{usrRptIdNo}
AND tdrid.usr_rpt_ln_id_no = #{usrRptLnIdNo}
AND tdrid.use_yn = #{useYn}
AND tdrid.userId = #{userId}

mapper.update:
/** nims-mysql-mapper|updateCancelDsuseRptInfo-폐기보고정보 취소|julim  */
UPDATE tb_dsuse_rpt_info
   SET use_yn = 'N'
     , mdfcn_dt = DATE_FORMAT(now(), '%Y%m%d%H%i%s')
     , mdfr = #{rgtr}
 WHERE usr_rpt_id_no = #{refUsrRptIdNo}
   AND use_yn = 'Y'

/** nims-mysql-mapper|updateCancelDsuseMgt-폐기관리 취소(참조사용자식별번호로)|julim  */
UPDATE tb_dsuse_mgt
   SET usr_rpt_id_no = #{usrRptIdNo}
     , use_yn = IF(#{rptTyCd} = '1', 'N', use_yn)
     , mdfcn_dt = DATE_FORMAT(now(), '%Y%m%d%H%i%s')
     , mdfr = #{rgtr}
 WHERE usr_rpt_id_no = #{refUsrRptIdNo}
   AND use_yn = 'Y'

/** nims-mysql-mapper|updateCancelDsuseRptInfoDtl-폐기보고정보 상세 취소|julim  */
UPDATE tb_dsuse_rpt_info_dtl
   SET use_yn = 'N'
     , mdfcn_dt = DATE_FORMAT(now(), '%Y%m%d%H%i%s')
     , mdfr = #{rgtr}
 WHERE usr_rpt_id_no = #{refUsrRptIdNo}
   AND use_yn = 'Y'

mapper.select:
/** nims-mysql-mapper|recusiveRefUsrRptIdNo-참조번호 recurive 조회|julim  */
SELECT usr_rpt_id_no as usrRptIdNo
     , ref_usr_rpt_id_no as refUsrRptIdNo
     , rpt_ty_cd as rptTyCd
  FROM tb_dsuse_rpt_info
 WHERE usr_rpt_id_no = #{refUsrRptIdNo}
